let i="http://proyecto-laravel.test/";window.addEventListener("DOMContentLoaded",function(){if(document.querySelectorAll(".like-btn")){let r=document.querySelectorAll(".like-btn");const c=(t,n)=>{fetch(i+"like/save/"+t).then(function(e){return e.json()}).then(function(){fetch(i+"image/likecount/"+t).then(function(e){return e.json()}).then(function(e){Number(e.count)>=0&&document.querySelector(`[data-id="${n}"]`)&&(document.querySelector(`[data-id="${n}"]`).textContent=e.count)})})};r.forEach(t=>{t.addEventListener("click",function(n){let e=n.target.id,o=n.target.nextElementSibling.dataset.id;t.classList.contains("btn-liked")?(t.setAttribute("src",i+"img/light-heart.png"),t.classList.remove("btn-liked"),c(e,o)):(t.setAttribute("src",i+"img/red-heart.png"),t.classList.add("btn-liked"),c(e,o))})})}});
